(function loadScripts() {
  const jsFiles = [
    "./js/awp/default-excel-config.js",
    "./js/awp/install-panel.js",
    "./js/awp/index.js",
    "./js/awp/render-panels-templates.js",
    "./js/awp/latln-converter.js",
    "./js/awp/load-canvas.js",
    "./js/awp/indexed-db.js",
    "./js/awp/gltf-loader.js",
    "./js/awp/file-loader.js",
    "./js/awp/data-validator.js",
    "./js/awp/project-selection-menu.js",
    "./js/awp/js-include-html.js",
    "./js/awp/local-object-store.js",
    "./js/awp/notes.js"
  ];

  const modulesFiles = [
    "./js/awp/load-canvas.js",
    "./js/awp/indexed-db.js",
    "./js/awp/gltf-loader.js",
  ];

  // Adding the script tag to the head as suggested before
  var scriptsLoader = document.getElementById("scriptsLoader");

  jsFiles.forEach((url) => {
    var script = document.createElement("script");

    if (modulesFiles.includes(url)) {
      script.type = "module";
    } else {
      script.type = "text/javascript";
    }

    script.src = url;

    // Fire the loading
    scriptsLoader.appendChild(script);
  });
})();
